    def registrar(self):
        self.limpiar()
        if self.Seleccion.isVisible():
            self.Seleccion.hide()
        else:
            self.Registro_Usuario.show()

    def limpiar(self):
        self.Text_usu.clear()
        self.Text_pass.clear()

    def logear(self):        
        if self.Registro_Usuario.isVisible():
            self.Registro_Usuario.close()#.hide
        
        if path.exists("dats.xd"):

            valid = valid_login(self.Text_usu.text().strip(),self.Text_pass.text().strip())
            if type(valid)==int:
                boton_info("Ha intentado ingresar demasiadas veces, intente de nuevo en {} segundos".format(valid))        
                
            elif valid:
                self.ui_selec.setupUi(self.Seleccion)
                self.Seleccion.show()
                self.limpiar()
            else:
                boton_error("Usuario o Contraseña inválidos. Verifique los campos")
        else:
            boton_error("No existen usuarios registrados")

