    def limpiar(self):
        self.Text_confpass.clear()
        self.Text_pass.clear()
        self.Text_usu.clear()

    def regresar(self):
        self.limpiar()
    
    def registrar(self):

        val_us=valid_usu_reg(self.Text_usu.text().strip())
        if val_us!=True:
            boton_error(val_us)
        else:
            if self.Text_pass.text().strip() != self.Text_confpass.text().strip():
                boton_error("Las contraseñas no coinciden")
            else:
                valid_pas=isStrongPass(self.Text_pass.text().strip())
                if type(valid_pas) == bool:
                    
                    new_user(self.Text_usu.text().strip(),self.Text_pass.text().strip())
                    self.limpiar()
                    boton_info("Usuario Registrado Exitosamente")
                    self.Boton_Regresar.click()
                    
                else:
                    boton_error(valid_pas)
    

